import React, { useState } from 'react';
import { debounce } from 'lodash';
import { useDispatch } from 'react-redux';
import { updateUserInput } from './userInputSlice';

function UserInputForm() {
  const [hourlyWage, setHourlyWage] = useState(null);
  const [hoursWorking, setHoursWorking] = useState(null);
  const [livingCosts, setLivingCosts] = useState(null);

  const dispatch = useDispatch();

  const handleChange = async (e) => {
    setHourlyWage(e.target.value);

    dispatch(updateUserInput({
      hourlyWage,

    }));
  };

  return (
    <div style={{
      backgroundColor: 'lightBlue', padding: '42px', borderRadius: '15px', display: 'flex', justifyContent: 'space-between', width: '61vw', margin: '42px',
    }}
    >
      <input placeholder="hourlyWage" defaultValue={hourlyWage} onChange={debounce(handleChange, 1000)} />
      <input placeholder="hoursWorking" defaultValue={hoursWorking} onChange={debounce(handleChange, 1000)} />
      <input placeholder="livingCosts" defaultValue={livingCosts} onChange={debounce(handleChange, 1000)} />
    </div>
  );
}

export default UserInputForm;
